document.addEventListener("DOMContentLoaded",function(){window.addEventListener("load",function(){const imageUrls=["/Assets/OnlyCars/Gallery/fullsize/1.png","/Assets/OnlyCars/Gallery/fullsize/2.png","/Assets/OnlyCars/Gallery/fullsize/3.png"];imageUrls.forEach(url=>{const img=new Image();img.src=url})});const galleryImages=document.querySelectorAll(".gallery img");const modal=document.getElementById("modal");const modalImg=document.getElementById("modalImg");const closeBtn=document.getElementById("closeBtn");const downloadBtn=document.getElementById("downloadBtn");galleryImages.forEach(img=>{img.addEventListener("click",function(){modal.classList.add("active");const fullSizeSrc=this.src.replace("/thumb/","/fullsize/");modalImg.src=fullSizeSrc;downloadBtn.href=this.src.replace("/thumb/","/raw/")})});modal.addEventListener("click",function(){modal.classList.remove("active")});closeBtn.addEventListener("click",function(event){event.stopPropagation();modal.classList.remove("active")});document.addEventListener("keydown",function(event){if(event.key==="Escape"){modal.classList.remove("active")}})})